import React, {Component} from 'react';
import {View, ScrollView, Text, Image, TouchableHighlight, TouchableOpacity, TouchableWithoutFeedback, TouchableNativeFeedback, ImageBackground} from 'react-native';

export default class MainComponent extends Component{

    constructor(){
        super();
        // 이 컴포넌트의 특별한 멤버변수 state
        this.state={
            img: require('./imgs/moana03.jpg'),

            // 이미지를 순차적으로 변경하기 위해 
            imgNum:0,  //배열의 인덱스번호
            imgArr: [
                require('./imgs/moana01.jpg'), 
                require('./imgs/moana02.jpg'), 
                require('./imgs/moana03.jpg'),
                require('./imgs/moana04.jpg'),
                require('./imgs/moana05.jpg'),
                // 네트워크도 당연히 사용 가능
                {uri:'data:image/jpeg;base64,'},
                {uri:'data:image/jpeg;base64,'},
            ],
        };
    }

    render(){
        return (
            <View style={{flex:1, padding:16}}>
                {/* 이미지가 많을 때 스크롤 필요 */}
                <ScrollView style={{height:300}}>
                    {/* require는 기본적으로 이미지크기에 맞춤 */}
                    <Image
                        style={{width:150, height:150, resizeMode:'contain'}} 
                        source={require('./imgs/moana01.jpg')}>
                    </Image>

                    {/* 네트워크의 이미지 보여주기: 사이즈지정이 없으면 보이지 않음  */}
                    <Image
                        style={{width:150, height:150}}
                        source={{uri:'data:image/jpeg;base64,'}}>
                    </Image>

                    {/* 이미지 클릭 반응하기: Button이 아니면 onPress속성은 사용할 수 없음 */}
                    {/* 터치를 인지하도록 만들어진 컴포넌트들 TouchableXXXX 를 사용 */}
                    {/* TouchableHighlight: 버튼을 누르면 뷰의 배경이 어두워지는 피드백 (웹상의 링크에 주로 사용) */}
                    {/* TouchableNativeFeedback: 버튼을 누르면 물결이 퍼지는 효과의 피드백 */}
                    {/* TouchableOpacity 버튼을 누르고 있는 동안 뒤의 배경이 보이도록 Opacity(불투명도)를 줄이는 피드백 */}
                    {/* TouchableWithoutFeedback: 어떤 피드백도 원하지 않을 때 사용 */}
                    {/* 롱터치는 Touchable들의 onLongPress 속성 사용 */}
                    <TouchableHighlight onPress={this.clickImage}>
                        <Image
                            style={{width:150, height:150, resizeMode:'contain'}} 
                            source={require('./imgs/moana02.jpg')}>
                        </Image>
                    </TouchableHighlight>
                    {/* Button이 아닌 컴포넌트들을 클릭하고 싶을 때 모두 사용 가능 */}

                    {/* <TouchableOpacity onPress={this.changeImage}>
                        <Image
                            style={{width:150, height:150, resizeMode:'contain'}} 
                            source={this.state.imgArr[this.state.imgNum]}>
                        </Image>
                    </TouchableOpacity>

                    <TouchableWithoutFeedback onPress={this.changeImage}>
                        <Image
                            style={{width:150, height:150, resizeMode:'contain'}} 
                            source={this.state.imgArr[this.state.imgNum]}>
                        </Image>
                    </TouchableWithoutFeedback> */}

                    <TouchableNativeFeedback background={TouchableNativeFeedback.SelectableBackground()} onPress={this.changeImage}>
                        <Image
                            style={{width:150, height:150, resizeMode:'contain'}} 
                            source={this.state.imgArr[this.state.imgNum]}>
                        </Image>
                    </TouchableNativeFeedback>
                </ScrollView>
            </View>

            // <View>
            //     {/* 이미지 배경 사용하기 */}
            //     <ImageBackground source={require('./imgs/moana01.jpg')} style={{width:'100%', height:'100%'}}>
            //         <Text style={{color:'white', fontSize:32, fontWeight:'bold', margin:16,}}>Image</Text>
            //         <Text style={{color:'orange', fontSize:24, margin:8,}}>This is background image test</Text>
            //     </ImageBackground>
            // </View>
        );
    }// render method

    clickImage= ()=>{
        alert('clicked image');
    }

    changeImage = ()=>{
        // 이미지의 경로 변경이 아니라 require()함수를 통째로 변경
        let num = this.state.imgNum;
        num++;
        if(num>6) num=0;
        this.setState({imgNum: num});
    }

}// MainComponent class